import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './ChainlinkFeeds.css';

function ChainlinkFeeds({ account, provider, signer, isConnected, currentNetwork }) {
  const [feeds, setFeeds] = useState([]);
  const [selectedFeeds, setSelectedFeeds] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');

  // Common Chainlink price feeds for different networks
  const availableFeeds = {
    '0x5': [ // Goerli
      { address: '0xD4a33860578De61DBAbDc8BFdb98FD742fA7028e', name: 'ETH/USD', description: 'Ethereum to USD Price Feed', decimals: 8, category: 'crypto' },
      { address: '0x779877A7B0D9E8603169DdbD7836e478b4624789', name: 'LINK/USD', description: 'Chainlink to USD Price Feed', decimals: 8, category: 'crypto' },
      { address: '0xAb5c49cFd6e8f85aF7dD4D75633ba265e4e4D7e9', name: 'BTC/USD', description: 'Bitcoin to USD Price Feed', decimals: 8, category: 'crypto' },
      { address: '0x7b219F57a8e9C7303204Af8eD0A4e6067Dd1C087', name: 'USDC/USD', description: 'USDC to USD Price Feed', decimals: 8, category: 'stablecoin' },
      { address: '0x7aFcF7B5D9b5F8c5C1d3C3d3C3d3C3d3C3d3C3d', name: 'DAI/USD', description: 'DAI to USD Price Feed', decimals: 8, category: 'stablecoin' }
    ],
    '0xaa36a7': [ // Sepolia
      { address: '0x694AA1769357215DE4FAC081bf1f309aDC325306', name: 'ETH/USD', description: 'Ethereum to USD Price Feed', decimals: 8, category: 'crypto' },
      { address: '0xc59E3633BAAC79493d08e8bD3f9731fD61B3C326', name: 'LINK/USD', description: 'Chainlink to USD Price Feed', decimals: 8, category: 'crypto' },
      { address: '0x1b44F3514812d835EB1BDB2acE6093a1F3e060A9', name: 'BTC/USD', description: 'Bitcoin to USD Price Feed', decimals: 8, category: 'crypto' },
      { address: '0x7aFcF7B5D9b5F8c5C1d3C3d3C3d3C3d3C3d3C3d', name: 'USDC/USD', description: 'USDC to USD Price Feed', decimals: 8, category: 'stablecoin' },
      { address: '0x7aFcF7B5D9b5F8c5C1d3C3d3C3d3C3d3C3d3C3d', name: 'DAI/USD', description: 'DAI to USD Price Feed', decimals: 8, category: 'stablecoin' }
    ],
    '0x13881': [ // Mumbai
      { address: '0x0715A7794a1dc8e42615F059dD6e406A6594651A', name: 'ETH/USD', description: 'Ethereum to USD Price Feed', decimals: 8, category: 'crypto' },
      { address: '0x12162c3E810393dECEc62aA165f3Df2c08FbF4E8', name: 'LINK/USD', description: 'Chainlink to USD Price Feed', decimals: 8, category: 'crypto' },
      { address: '0x007A22900a3B98143368Bd7874Bc93c557e4e408', name: 'BTC/USD', description: 'Bitcoin to USD Price Feed', decimals: 8, category: 'crypto' },
      { address: '0x7aFcF7B5D9b5F8c5C1d3C3d3C3d3C3d3C3d3C3d', name: 'USDC/USD', description: 'USDC to USD Price Feed', decimals: 8, category: 'stablecoin' },
      { address: '0x7aFcF7B5D9b5F8c5C1d3C3d3C3d3C3d3C3d3C3d', name: 'DAI/USD', description: 'DAI to USD Price Feed', decimals: 8, category: 'stablecoin' }
    ]
  };

  // Chainlink ABI for price feeds
  const chainlinkABI = [
    "function latestRoundData() external view returns (uint80 roundId, int256 answer, uint256 startedAt, uint256 updatedAt, uint80 answeredInRound)",
    "function decimals() external view returns (uint8)",
    "function description() external view returns (string memory)"
  ];

  useEffect(() => {
    if (currentNetwork && currentNetwork.chainId) {
      const networkFeeds = availableFeeds[currentNetwork.chainId] || [];
      setFeeds(networkFeeds);
    }
  }, [currentNetwork]);

  // Get live price from Chainlink feed
  const getLivePrice = async (feedAddress) => {
    if (!provider || !isConnected) return null;

    try {
      const contract = new ethers.Contract(feedAddress, chainlinkABI, provider);
      const roundData = await contract.latestRoundData();
      const decimals = await contract.decimals();

      // Convert answer to readable price
      const price = roundData.answer / Math.pow(10, decimals);
      return price;
    } catch (error) {
      console.error('Error fetching price from feed:', error);
      return null;
    }
  };

  // Refresh all feed prices
  const refreshPrices = async () => {
    if (!isConnected) return;

    setLoading(true);
    try {
      const updatedFeeds = await Promise.all(
        feeds.map(async (feed) => {
          const price = await getLivePrice(feed.address);
          return { ...feed, currentPrice: price };
        })
      );
      setFeeds(updatedFeeds);
    } catch (error) {
      console.error('Error refreshing prices:', error);
    } finally {
      setLoading(false);
    }
  };

  // Toggle feed selection
  const toggleFeedSelection = (feed) => {
    setSelectedFeeds(prev => {
      const isSelected = prev.some(f => f.address === feed.address);
      if (isSelected) {
        return prev.filter(f => f.address !== feed.address);
      } else {
        return [...prev, feed];
      }
    });
  };

  // Filter feeds based on search and category
  const filteredFeeds = feeds.filter(feed => {
    const matchesSearch = feed.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         feed.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = filterCategory === 'all' || feed.category === filterCategory;
    return matchesSearch && matchesCategory;
  });

  // Get category display name
  const getCategoryDisplayName = (category) => {
    const categories = {
      'crypto': 'Cryptocurrencies',
      'stablecoin': 'Stablecoins',
      'forex': 'Forex',
      'commodities': 'Commodities'
    };
    return categories[category] || category;
  };

  return (
    <div className="chainlink-feeds">
      <div className="feeds-header">
        <h2>🔗 Chainlink Price Feeds</h2>
        <p>Select and monitor real-time price feeds from Chainlink oracles</p>
      </div>

      {/* Network Info */}
      {currentNetwork && (
        <div className="network-info-banner">
          <span className="network-badge">
            🌐 {currentNetwork.name}
          </span>
          {!isConnected && (
            <span className="warning-badge">
              ⚠️ Connect wallet to view live prices
            </span>
          )}
        </div>
      )}

      {/* Controls */}
      <div className="feeds-controls">
        <div className="search-filter">
          <input
            type="text"
            placeholder="Search feeds..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />

          <select
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
            className="category-filter"
          >
            <option value="all">All Categories</option>
            <option value="crypto">Cryptocurrencies</option>
            <option value="stablecoin">Stablecoins</option>
            <option value="forex">Forex</option>
            <option value="commodities">Commodities</option>
          </select>
        </div>

        <button
          onClick={refreshPrices}
          disabled={!isConnected || loading}
          className="refresh-button"
        >
          {loading ? '🔄 Refreshing...' : '🔄 Refresh Prices'}
        </button>
      </div>

      {/* Feeds Grid */}
      <div className="feeds-grid">
        {filteredFeeds.length === 0 ? (
          <div className="no-feeds">
            <p>No feeds available for the current network</p>
            <p>Switch to a supported testnet (Goerli, Sepolia, or Mumbai)</p>
          </div>
        ) : (
          filteredFeeds.map((feed) => (
            <div
              key={feed.address}
              className={`feed-card ${selectedFeeds.some(f => f.address === feed.address) ? 'selected' : ''}`}
              onClick={() => toggleFeedSelection(feed)}
            >
              <div className="feed-header">
                <h3>{feed.name}</h3>
                <span className="category-tag">{getCategoryDisplayName(feed.category)}</span>
              </div>

              <p className="feed-description">{feed.description}</p>

              <div className="feed-details">
                <div className="detail-row">
                  <span className="label">Address:</span>
                  <span className="value address">
                    {feed.address.slice(0, 6)}...{feed.address.slice(-4)}
                  </span>
                </div>

                <div className="detail-row">
                  <span className="label">Decimals:</span>
                  <span className="value">{feed.decimals}</span>
                </div>

                <div className="detail-row">
                  <span className="label">Current Price:</span>
                  <span className="value price">
                    {feed.currentPrice !== undefined ? (
                      `$${feed.currentPrice.toFixed(4)}`
                    ) : (
                      isConnected ? 'Loading...' : 'Connect wallet'
                    )}
                  </span>
                </div>
              </div>

              <div className="feed-actions">
                <button
                  className="select-button"
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleFeedSelection(feed);
                  }}
                >
                  {selectedFeeds.some(f => f.address === feed.address) ? '✓ Selected' : 'Select'}
                </button>

                <button
                  className="copy-address-button"
                  onClick={(e) => {
                    e.stopPropagation();
                    navigator.clipboard.writeText(feed.address);
                    alert('Address copied to clipboard!');
                  }}
                >
                  📋 Copy
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Selected Feeds Summary */}
      {selectedFeeds.length > 0 && (
        <div className="selected-feeds-summary">
          <h3>Selected Feeds ({selectedFeeds.length})</h3>
          <div className="selected-feeds-list">
            {selectedFeeds.map((feed) => (
              <div key={feed.address} className="selected-feed-item">
                <span className="feed-name">{feed.name}</span>
                <span className="feed-price">
                  {feed.currentPrice !== undefined ? `$${feed.currentPrice.toFixed(4)}` : 'N/A'}
                </span>
                <button
                  onClick={() => toggleFeedSelection(feed)}
                  className="remove-button"
                >
                  ✕
                </button>
              </div>
            ))}
          </div>

          <div className="summary-actions">
            <button
              className="export-button"
              onClick={() => {
                const feedData = selectedFeeds.map(f => ({
                  name: f.name,
                  address: f.address,
                  description: f.description,
                  decimals: f.decimals,
                  category: f.category
                }));
                const blob = new Blob([JSON.stringify(feedData, null, 2)], { type: 'application/json' });
                const url = URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.href = url;
                a.download = 'selected-chainlink-feeds.json';
                a.click();
                URL.revokeObjectURL(url);
              }}
            >
              📥 Export Selected Feeds
            </button>

            <button
              className="clear-button"
              onClick={() => setSelectedFeeds([])}
            >
              🗑️ Clear All
            </button>
          </div>
        </div>
      )}

      {/* Help Section */}
      <div className="help-section">
        <h3>💡 How to Use</h3>
        <div className="help-content">
          <div className="help-item">
            <strong>1. Connect Wallet:</strong> Connect your Web3 wallet to view live prices
          </div>
          <div className="help-item">
            <strong>2. Select Feeds:</strong> Click on feeds to select them for your project
          </div>
          <div className="help-item">
            <strong>3. Copy Addresses:</strong> Use the copy button to get the contract address
          </div>
          <div className="help-item">
            <strong>4. Export Selection:</strong> Download your selected feeds as JSON
          </div>
        </div>
      </div>
    </div>
  );
}

export default ChainlinkFeeds;
